import './App.css';
import { useEffect, useState } from 'react'

// API url ->
const API_URL = 'http://localhost:3001/todos';

function App() {
  const [todoData, setTodoData] = useState([]);

  const fetchData = async () => {
    const response = await fetch(API_URL);
    const data = await response.json();

    setTodoData(data);
  }

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="App">
      <br />
      {
        todoData.map((todo_) =>
          (<ToDo data={todo_} />)
        )
      }

      <CreateToDo />
    </div>
  );
}

function ToDo({ data }) {
  return (
    <div className='TodoParent'>
      <div className='Todo'>
        <input type='checkbox' checked={data.todo.done} className='Todo_Done'></input>
        <h3 className='Todo_Title'>{data.todo.desc}</h3>
      </div>
    </div>
  )
}

function CreateToDo() {
  function OnClickPost() {
    if (document.getelementsbyclassname('CreateTodo_Desc').value == null)
      return;

    fetch(API_URL, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        "todo": {
          "done": document.getelementsbyclassname('CreateTodo_Done').value,
          "desc": document.getelementsbyclassname('CreateTodo_Desc').value
        }
      })
    })
  }

  return (
    <div className='TodoParent'>
      <div className='Todo'>
        <input type='checkbox' className='CreateTodo_Done'></input>
        <input placeholder='Write ToDo' className='CreateTodo_Desc'></input>
        <button onClick={() => OnClickPost()}>POST</button>
      </div>
    </div>
  )
}

export default App;
