import './App.css';
import { useEffect, useState } from 'react'

// API url ->
const API_URL = 'http://localhost:3001/todos';

function App() {
  const [todoData, setTodoData] = useState([]);

  const fetchData = async () => {
    const response = await fetch(API_URL);
    const data = await response.json();

    setTodoData(data);
  }

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="App">
      {
        todoData.map((todo_) =>
          (<ToDo data={todo_} />)
        )
      }
    </div>
  );
}

function ToDo({ data }) {
  return (
    <div className='Todo'>
      <input type='checkbox' checked={data.todo.done} className='Todo_Done'></input>
      <h3>{data.todo.desc}</h3>
    </div>
  )
}

export default App;
