import './App.css';
import { useEffect, useState } from 'react'

// API url ->
const API_URL = 'http://localhost:3001/todos';

function App() {
  const [todoData, setTodoData] = useState([]);

  const fetchData = async () => {
    const response = await fetch(API_URL);
    const data = await response.json();

    setTodoData(data);
  }

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="App">
      <br />
      {
        todoData.map((todo_) =>
          (<ToDo data={todo_} />)
        )
      }
    </div>
  );
}

function ToDo({ data }) {
  return (
    <div className='TodoParent'>
      <div className='Todo'>
        <input type='checkbox' checked={data.todo.done} className='Todo_Done'></input>
        <h3 className='Todo_Title'>{data.todo.desc}</h3>
      </div>
    </div>
  )
}

function CreateToDo() {
  return (
    <div className='TodoParent'>
      <div className='Todo'>
        <input type='checkbox' className='Todo_Done'></input>
        <input placeholder='Write ToDo' ></input>
      </div>
    </div>
  )
}

export default App;
