import './App.css';
import { useEffect, useState } from 'react'

//IMAGES
import DeleteImg from './Images/Delete.svg';
// API url ->
const API_URL = 'http://localhost:3001/todos';

function App() {
  const [todoData, setTodoData] = useState([]);

  const fetchData = async () => {
    const response = await fetch(API_URL);
    const data = await response.json();

    setTodoData(data);
  }

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="App">
      <br />
      {
        todoData.map((todo_) =>
          (<ToDo data={todo_} />)
        )
      }

      {/* Post new ToDo */}
      <CreateToDo />
    </div>
  );
}

function ToDo({ data }) {
  return (
    <div className='TodoParent'>
      <div className='Todo'>
        <input type='checkbox' checked={data.todo.done === true ? true : false} className='Todo_Done'></input>
        <h3 className='Todo_Title'>{data.todo.desc}</h3>

        <button className='DeleteTodo' onClick={() => DeleteToDo(data._id)} > <img src={DeleteImg}></img> </button>
      </div>
    </div>
  )
}

// Post new ToDo
function CreateToDo() {
  const [todoDesc, setTodoDesc] = useState("");
  const [todoDone, setTodoDone] = useState(false);

  function OnClickPost() {
    if (todoDesc === null || todoDesc === "")
      return;

    // Post Api
    fetch(API_URL, {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        "todo": {
          "done": todoDone,
          "desc": todoDesc
        }
      })
    })

    // Make it empty
    setTodoDesc("")
    setTodoDone(false)
  }

  // HTML
  return (
    <div className='TodoParent'>
      <div className='Todo'>
        <input type='checkbox' className='CreateTodo_Done' checked={todoDone} onChange={(e) => { setTodoDone(e.target.checked) }}></input>

        <input placeholder='Write ToDo' className='CreateTodo_Desc' value={todoDesc} onChange={(e) => { setTodoDesc(e.target.value) }}></input>

        <button onClick={() => OnClickPost()}>POST</button>
      </div>
    </div>
  )
}

// Delete ToDo using id
function DeleteToDo(id) {
  // Delete Api
  fetch(API_URL + "?id=" + id, {
    method: 'DELETE'
  })
}

export default App;
